(()=>{let e=document.querySelector("#search-text"),t=document.querySelector("#search-button"),o=document.querySelector("#icon-display");t.addEventListener("click",(function(){fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e.value}&APPID=ce678a1ab136ef46871798383c6d8d08`,{mode:"cors"}).then((e=>{if(console.log(e),console.log(`Response Code: ${e.status}`),200==e.status)return e.json()})).then((e=>{console.log(e),function(e){let t=e.weather[0].icon;o.setAttribute("src",`http://openweathermap.org/img/wn/${t}@4x.png`)}(e)}))}))})();